import {
  playMusicUrl,
  getSongLyric,
  getSongDetails,
} from "@/api/api";

 //添加歌曲到播放列表
 export function playMusic (id, fee, index , list , that){
	//获取播放音乐链接
	console.log(fee,'----')
	if (that[list][index].timer) {
		console.log(fee,'----')
		if (fee == 1) {
			that.$message({
				message: "VIP歌曲 - 只能试听30s",
				type: "warning",
			});
		}
		playMusicUrl({
			id: id,
		}).then((res) => {
			// console.log("播放音乐链接", res.data.data[0].url);
			if (res.data.data[0].url) {
				that.songUrlAdd = res.data.data[0].url;
			} else {
				that.$message({
					message: "这首歌暂无版权",
					type: "error",
				});
				that[list][index].timer = false;
				setTimeout(() => {
					that[list][index].timer = true;
				}, 3000);
				return;
			}
		}),
			// 获取歌词
			getSongLyric({
				id: id,
			}).then((res) => {
				that.songlrc = res.data.lrc.lyric;
			}),
			// 获取歌曲信息
			getSongDetails({
				ids: id,
			}).then((res) => {
				// console.log("获取歌曲信息，添加到播放器：", res.data.songs[0]);
				if (that.songUrlAdd != null) {
					that.$store.commit("addSONG", {
						name: res.data.songs[0].name, //歌曲名
						artist: res.data.songs[0].ar[0].name, //作者
						url: that.songUrlAdd,
						cover: res.data.songs[0].al.picUrl,
						lrc: that.songlrc,
						id: id,
						// theme: that.randomColor(),
					});
					// console.log(
					//   that.$store.state.audio,
					//   "添加歌曲后的歌曲播放列表-----"
					// );
					that.songUrlAdd = null;
					that.songlrc = "";
					that[list][index].timer = false;
					setTimeout(() => {
						that[list][index].timer = true;
					}, 3000);
				}
			});
	}
}
