import { getNewMvList } from "@/api/api";
import { transPlayCount } from "@/utils/commonApi";
export default {
  data() {
    return {
      limit: 4,
      recMvList: []
    };
  },

  mounted() {
    this.getNewMvList();
  },

  methods: {
    getNewMvList() {
      //获取最新MV
      var that = this;
      let params = {
        limit: that.limit
      };
      getNewMvList(params).then(res => {
        that.recMvList = res.data.data; // console.log("最新mv---:", res.data.data);
        //将播放量转换成亿万单位

        transPlayCount(that.recMvList, "playCount");
      });
    },

    goPlayMv(mvId) {
      var that = this;
      that.$router.push({
        name: "mvPlay",
        params: {
          mvId: mvId
        }
      });
    }

  }
};
