import { getDownloadUrl } from "@/api/api";
import { playMusic, listenMusic } from "@/utils/musicPlay";
import { likeMusic } from "@/api/needLogin/musicOperate";
import { download } from "@/utils/commonApi";
export default {
  props: {
    songlist: Array
  },

  data() {
    return {};
  },

  methods: {
    //获取歌曲详情,进入详情页面
    goSongDetails(songDetails) {
      // console.log('歌曲信息',songDetails)
      this.$router.push({
        name: "songDetails",
        params: {
          songDetails: songDetails
        }
      });
    },

    //加入/删除喜欢音乐
    likeMusic(id, bool) {
      const loading = this.$loading({
        target: "#app_body",
        background: "rgba(255,255,255,0.1)"
      });
      likeMusic({
        id: id,
        like: bool
      }).then(res => {
        this.$nextTick(() => {
          loading.close();
        }); // console.log("添加", res);

        if (res.data.code === 200) {
          if (bool) {
            this.$message.success("已添加入我的喜欢-可进入网易云音乐查看");
            return;
          }

          this.$message.success("已从我的喜欢列表删除");
        }
      }).catch(error => {
        this.$nextTick(() => {
          loading.close();
        });
      });
    },

    //试听音乐
    listenMusic(songDetals) {
      //获取播放音乐链接
      var that = this;
      var list = "songlist";
      listenMusic(songDetals, that);
    },

    //添加歌曲到播放列表
    playMusic(songDetals) {
      //获取播放音乐链接
      var that = this;
      var list = "songlist";
      playMusic(songDetals, that);
    },

    playMV(mvId) {
      //获取mv播放链接
      this.$router.push({
        name: "mvPlay",
        params: {
          mvId: mvId
        }
      });
    },

    //获取歌曲下载地址
    getDownloadUrl(songId, songName) {
      var that = this;
      that.$message({
        type: "success",
        message: "正在尝试下载"
      });
      let params = {
        id: songId
      };
      getDownloadUrl(params).then(res => {
        // console.log('歌曲下载地址：', res.data)
        // console.log("歌曲下载地址：", res.data.data.url);
        // download(res.data.data.url, songName)
        download(res.data.data[0].url, songName);
        that.$message({
          type: "success",
          message: "开始下载了"
        });
      });
    }

  }
};
