import { cloudMusic } from '@/api/request'

//获取用户详情
export function getuserDetail(params) {//传入uid
	return cloudMusic({
		method: 'get',
		url: '/user/detail',
		params: params,
	})
}
//账号信息
export function getAccount(params) {//传入uid
	return cloudMusic({
		method: 'get',
		url: '/user/account',
		params: params,
	})
}
