export default {
  name: "MaskCanvas",
  data: function data() {
    return {
      canvas: "",
      ctx: ""
    };
  },
  mounted: function mounted() {
    var that = this;
    that.canvas = document.getElementById("myCanvas");
    that.ctx = that.canvas.getContext("2d"); // console.log("canvas", that.canvas);
    // console.log("ctx", that.ctx);

    that.canvas.width = window.innerWidth;
    that.canvas.height = window.innerHeight;
    window.onresize = that.syncWH();
  },
  methods: {
    //同步画布宽高
    syncWH: function syncWH() {
      this.canvas.width = window.innerWidth;
      this.canvas.height = window.innerHeight;
    },
    //随机函数
    randomNum: function randomNum(m, n) {
      return Math.floor(Math.random() * (n - m + 1) + m);
    },
    //随机颜色
    randomColor: function randomColor() {
      return "rgb(" + randomNum(0, 255) + "," + randomNum(0, 255) + "," + randomNum(0, 255) + ")";
    }
  }
};
