import { getPlaylistDetail, getSongList, getPlayListComment } from "@/api/api";
import { collectPlaylist, deletePlayList } from "@/api/needLogin/musicOperate";
import { transMusicTime } from "@/utils/commonApi";
import songlist from "@/components/songlist.vue";
export default {
  components: {
    songlist
  },

  data() {
    return {
      //歌单id
      songListId: "",
      //评论分页
      currentPage: 1,
      // 歌曲数组
      musicList: [],
      //下载音乐的地址
      DownloadSongUrl: "",
      //mv地址
      mvUrl: "",
      //搜索歌曲总数
      count: "",
      playListComment: [{
        user: {
          avatarUrl: ""
        }
      }],
      // commentCount: "",
      //MV
      songMV: [],
      //歌单详情
      playListDetail: {
        name: "",
        coverImgUrl: "",
        trackCount: "",
        creator: {
          avatarUrl: "",
          nickname: "",
          signature: ""
        },
        tags: [],
        description: ""
      },
      userId: "" //用户id，用于鉴别歌单是否由用户自己创建的

    };
  },

  mounted() {
    if (localStorage.getItem("user")) {
      this.userId = JSON.parse(localStorage.getItem("user")).userId;
    } //缓存id,解决params数据在刷新页面后丢失，导致无法获取到歌单id


    if (this.$route.params.playListDetail) {
      localStorage.setItem("playListDetail", JSON.stringify(this.$route.params.playListDetail)); //存储对象类型需要转换成字符串
    } // 判断是否使用缓存


    this.$route.params.playListDetail ? this.playListDetail = this.$route.params.playListDetail : this.playListDetail = JSON.parse(localStorage.getItem("playListDetail")); //将字符串转成对象

    this.getPlaylistDetail();
    this.getSongList(); // this.getPlayListComment();
  },

  methods: {
    /* //切换歌单评论
    playListHandleCurrentChange(currentPage) {
      var that = this;
      that.currentPage = currentPage;
      that.getPlayListComment();
    }, */

    /* //获取歌单评论
    getPlayListComment() {
      var that = this;
      let params = {
        id: that.playListDetail.id,
        limit: 7,
        offset: (that.currentPage - 1) * 7,
      };
      getPlayListComment(params).then(async (res) => {
        // console.log("歌单评论：", res.data);
        this.playListComment = await res.data.comments;
        that.commentCount = await res.data.total;
        // console.log("歌曲评论：", res.data);
      });
    }, */
    //分页
    handleCurrentChange: function (currentPage) {
      // console.log(`当前页: ${currentPage}`);
      this.currentPage = currentPage;
      var that = this;
      that.getPlaylistDetail(currentPage);
    },

    getPlaylistDetail() {
      //传入歌单id获取歌曲id和歌单详情
      var that = this;
      let params = {
        id: that.playListDetail.id
      };
      getPlaylistDetail(params).then(async res => {
        // console.log("----------------:", res.data.privileges); //歌单歌曲
        // console.log("--歌单详情-------:", res.data.playlist); //歌单歌曲
        that.playListDetail = await res.data.playlist; // that.musicList = that.playListDetail.tracks;
      });
    },

    getSongList() {
      //获取歌单所有歌曲
      var that = this;
      let params = {
        id: that.playListDetail.id
      };
      getSongList(params).then(async res => {
        that.musicList = await res.data.songs; //给每个列表添加一个防抖

        for (let item of that.musicList) {
          that.$set(item, "timer", true);
        }

        transMusicTime(that.musicList, "dt");
      });
    },

    //收藏/取消收藏歌单
    collectPlaylist(t) {
      const loading = this.$loading({
        target: "#list_detail",
        background: "rgba(255,255,255,0.1)"
      });
      let params = {
        t: t,
        id: this.playListDetail.id
      };
      collectPlaylist(params).then(res => {
        this.$nextTick(() => {
          loading.close();
        }); // console.log(res, "收藏返回");

        if (res.data.code === 200) {
          if (t === "1") {
            this.$message({
              type: "success",
              message: "收藏成功-可在网易云音乐中查看"
            });
            return;
          } else if (t === "2") {
            this.$message({
              type: "success",
              message: "已取消收藏"
            });
          }
        }
      }).catch(err => {
        this.$nextTick(() => {
          loading.close();
        }); //如果不写catch,请求拦截器拦截后这里会报错
      });
    },

    //删除歌单（自己的）
    deletePlayList(id) {
      const loading = this.$loading({
        target: "#list_detail",
        background: "rgba(255,255,255,0.1)"
      });
      deletePlayList({
        id: id
      }).then(res => {
        this.$nextTick(() => {
          loading.close();
        });

        if (res.data.code === 200) {
          this.$message.success("已从我的歌单删除");
          this.$router.go(-1);
        }
      }).catch(err => {
        this.$nextTick(() => {
          loading.close();
        });
      });
    }

  }
};
