import QRCode from "qrcode";
import { getqrKey, getqrImg, getqrStatus, getloginStatus, loginOut } from "@/api/login";
import { cookieParser, getTimestamp } from "@/utils/commonApi";
export default {
  name: "Login",

  data() {
    return {
      loginWrapShow: false,
      //展示登录窗口
      qrStatus: "",
      qrtimer: null,
      qrshowed: false,
      key: "",
      qrloading: false //点击判断是否已经授权时的加载图标状态

    };
  },

  computed: {
    user() {
      return this.$store.state.user;
    }

  },

  created() {// if (localStorage.getItem("user")) {
    //   this.user = JSON.parse(localStorage.getItem("user"));
    // }
    // this.getloginStatus();
  },

  methods: {
    goLogin() {
      //点击登录，获取二维码
      // this.loginWrapOnOff(true);
      this.loginWrapShow = true;
      this.refreshQr();
    },

    //生成二维码
    getQRCode(qrurl) {
      let opts = {
        errorCorrectionLevel: "H",
        //容错级别
        type: "image/png",
        //生成的二维码类型
        quality: 0.3,
        //二维码质量
        margin: 5,
        //二维码留白边距
        width: 180,
        //宽
        height: 180,
        //高
        text: "",
        //二维码内容
        color: {
          dark: "#333333",
          //前景色
          light: "#fff" //背景色

        }
      };
      this.QRCodeMsg = qrurl; //生成的二维码为URL地址js

      let msg = document.getElementById("QRCode_header"); // 将获取到的数据（val）画到msg（canvas）上

      QRCode.toCanvas(msg, qrurl, opts, function (error) {// console.log(error)
      });
      this.qrshowed = true;
    },

    //  获取/刷新二维码
    refreshQr() {
      this.qrshowed = false;
      let params = {
        timestamp: getTimestamp()
      }; // console.log(params.timestamp)

      getqrKey(params).then(async res => {
        this.key = await res.data.data.unikey;
        let params = {
          key: res.data.data.unikey
        };
        getqrImg(params).then(res => {
          this.getQRCode(res.data.data.qrurl);
        });
      });
    },

    // 二维码状态
    getqrStatus() {
      this.qrloading = true;
      let params = {
        key: this.key
      };
      getqrStatus(params).then(async res => {
        this.qrStatus = await res.data.message;

        if (res.data.code === 803) {
          // clearInterval(this.qrtimer);
          // console.log("扫码登录成功");
          let cookies = cookieParser(res.data.cookie);
          this.$store.commit("setTOKEN", cookies[1]); //设置好了token

          setTimeout(() => {
            this.loginWrapShow = false; //关闭登录窗口

            this.$message.success("已登录");
          }, 500);
          this.getloginStatus();
        } else if (res.data.code === 800) {
          this.qrStatus += ",请刷新获取";
        } else if (res.data.code === 400) {
          this.qrStatus = "请求错误";
        }

        this.qrloading = false;
      });
    },

    //获取登录状态
    getloginStatus() {
      getloginStatus().then(async res => {
        // console.log(res.data.data.profile);
        if (res.data.data.profile) {
          this.$store.commit("setUSER", {
            avatarUrl: res.data.data.profile.avatarUrl,
            nickname: res.data.data.profile.nickname,
            userId: res.data.data.profile.userId,
            userType: res.data.data.profile.userType
          });
        }
      });
    },

    //退出登录
    loginOut() {
      const loading = this.$loading({
        background: "rgba(255,255,255,0.1)"
      });
      loginOut().then(res => {
        this.$nextTick(() => {
          loading.close();
        });
        this.$store.commit("removeTOKEN");
        this.$message.success("已退出登录"); // this.getloginStatus(); //获取登陆状态
      }).catch(err => {
        this.$nextTick(() => {
          loading.close();
        });
      });
    },

    golike() {
      console.log(this.user);
      this.$router.push({
        name: "myPlayList",
        params: {
          id: this.user.userId
        }
      });
    }

  }
};
