import { cloudMusic } from '@/api/request'

//收藏/取消歌单（1收藏2取消）
export function collectPlaylist(params) {
	return cloudMusic({
		url: '/playlist/subscribe',
		method: 'post',
		data: params,
	})
}
/*
 喜欢音乐必选参数 : id: 歌曲 id
可选参数 : like: 布尔值 , 默认为 true 即喜欢 , 若传 false, 则取消喜欢
*/
export function likeMusic(params) {
	return cloudMusic({
		url: '/like',
		method: 'post',
		data: params,
	})
}

/* 获取喜欢音乐列表
说明 : 调用此接口 , 传入用户 id, 可获取已喜欢音乐 id 列表(id 数组)

必选参数 : uid: 用户 id

接口地址 : /likelist

调用例子 : /likelist?uid=32953014 */
export function getLikeList(params) {
	return cloudMusic({
		url: '/likelist',
		method: 'get',
		params: params,
	})
}
