import { getSuggestMvList } from "@/api/api";
import { transPlayCount } from "@/utils/commonApi";
export default {
  data() {
    return {
      limit: 4,
      recMvList: []
    };
  },

  mounted() {
    this.getSuggestMvList();
  },

  methods: {
    getSuggestMvList() {
      //获取推荐MV
      var that = this;
      let params = {
        limit: that.limit
      };
      getSuggestMvList(params).then(res => {
        // console.log(res);
        // console.log(res, 'tuijianmv')
        that.recMvList = res.data.result; // console.log("推荐mv---:", res.data.result);
        //将播放量转换成亿万单位

        transPlayCount(that.recMvList, "playCount");
      });
    },

    goPlayMv(mvId) {
      var that = this;
      that.$router.push({
        name: "mvPlay",
        params: {
          mvId: mvId
        }
      });
    }

  }
};
