        transPlayCount(that.recMvList, "playCount");
      });
    },
    goPlayMv: function goPlayMv(mvId) {
      var that = this;
      that.$router.push({
        name: "mvPlay",
        params: {
          mvId: mvId
        }
      });
    }
  }
};
