    getDownloadUrl: function getDownloadUrl(songId, songName) {
      var that = this;
      var params = {
        id: songId
      };

      _getDownloadUrl(params).then(function (res) {
        // console.log("歌曲下载地址：", res.data.data.url);
        download(res.data.data.url, songName);
        that.$message({
          type: "success",
          message: "开始下载了"
        });
      });
    },
    goVideo: function goVideo(vid, type) {
      if (type === 0) {
        this.$router.push({
          name: "mvPlay",
          query: {
            mvId: vid
          }
        });
      } else {
        this.$router.push({
          name: "videoPlay",
          query: {
            vid: vid
          }
        });
      }
    },
    randomColor: function randomColor() {
      return "#".concat((Math.random() * 0xffffff << 0).toString(16));
    }
  }
};
