import { getMVUrl, getcommentMV, getMvDetails, getMvDetailInfo } from "@/api/api";
export default {
  // props: ['mvId'],
  data: function data() {
    return {
      mvUrl: "",
      mvId: "",
      mvComments: [],
      mvDetail: {
        cover: ''
      },
      count: "",
      currentPage: 1
    };
  },
  mounted: function mounted() {
    //缓存id,解决params数据在刷新页面后丢失，导致无法获取到歌单id
    if (this.$route.params.mvId) {
      localStorage.setItem("mvId", this.$route.params.mvId);
    } // 判断是否使用缓存


    this.$route.params.mvId ? this.mvId = this.$route.params.mvId : this.mvId = localStorage.getItem("mvId");
    this.playMV();
  },
  methods: {
    //mv分页
    videohandleCurrentChange: function videohandleCurrentChange(currentPage) {
      // console.log(`当前页: ${currentPage}`);
      this.currentPage = currentPage; // console.log("我是第一"+this.currentPage)

      this.playMV(this.mvId, currentPage);
    },
    playMV: function playMV() {
      var _this = this;

      var params = {
        mvid: this.mvId
      };
      getMVUrl({
        id: this.mvId
      }).then(function (res) {
        _this.mvUrl = res.data.data.url;
      }); //mv详情

      getMvDetails(params).then(function (res) {
        // console.log('mv详情---：', res.data.data)
        _this.mvDetail = res.data.data; // mv点赞转发评论数数据

        getMvDetailInfo(params).then(function (res) {
          // console.log("mv点赞转发评论数数据---:", res.data);
          _this.$set(_this.mvDetail, "likedCount", res.data.likedCount);
        });
      }); // 热门评论;

      getcommentMV({
        id: this.mvId,
        limit: 8,
        offset: (this.currentPage - 1) * 8
      }).then(function (res) {
        _this.mvComments = res.data.comments; // console.log("我是评论" + this.mvComments);

        _this.count = res.data.total; // console.log("我是最后面"+page)
      });
    }
  }
};
